import React, { useState } from 'react';
import { PageContainer } from '../../Pagecontainer';
export function StudyGuide() {
    const [studyGuideData, setStudyGuideData] = useState({
        keyConcepts: ["Concept 1: Explanation", "Concept 2: Explanation"],
        definitions: ["Term 1: Definition", "Term 2: Definition"],
    });

    const handleSave = () => {
        localStorage.setItem('studyGuide', JSON.stringify(studyGuideData));
        alert("Study guide saved to local storage!");
    };
    return (
      <PageContainer>
          <h2 className="text-2xl font-semibold mb-4">Generated Study Guide</h2>
          <div className="space-y-4">
              <div className="bg-white p-4 rounded shadow-md">
                  <h3 className="text-xl font-semibold mb-2">Key Concepts</h3>
                  <p className="text-gray-700">
                      {/* Render key concepts here */}
                      - Concept 1: Explanation <br />
                      - Concept 2: Explanation <br />
                      {/* Placeholder examples */}
                  </p>
              </div>

              <div className="bg-white p-4 rounded shadow-md">
                  <h3 className="text-xl font-semibold mb-2">Definitions</h3>
                  <p className="text-gray-700">
                      {/* Render definitions here */}
                      - Term 1: Definition <br />
                      - Term 2: Definition <br />
                      {/* Placeholder examples */}
                  </p>
              </div>

              <div className="flex justify-end space-x-2 mt-4">
                  <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                      Save
                  </button>
                  <button className="bg-gray-300 text-gray-700 px-4 py-2 rounded hover:bg-gray-400">
                      Edit
                  </button>
              </div>
          </div>
      </PageContainer>
  );
}
