import React, { useState } from 'react';
import { Link } from 'react-router-dom';

export function Upload() {
    const [file, setFile] = useState(null);

    // Handle file selection
    const handleFileChange = (event) => {
        const selectedFile = event.target.files[0];
        setFile(selectedFile); // Store the file in state
        console.log("Selected file:", selectedFile);
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4">
            <h2 className="text-2xl font-semibold mb-4">Upload Notes</h2>
            <textarea className="w-full h-32 p-2 border border-gray-300 rounded mb-4" placeholder="Paste your notes here..."></textarea>

            {/* File upload input */}
            <input 
                type="file" 
                onChange={handleFileChange} 
                className="mb-4"
            />

            {/* Show selected file name if a file is uploaded */}
            {file && <p className="text-gray-600">Selected file: {file.name}</p>}

            <input type="url" className="w-full p-2 border border-gray-300 rounded mb-4" placeholder="Enter URL..." />

            {/* Link to navigate to the next page */}
            <Link to="/summarize">
                <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600">
                    Next
                </button>
            </Link>
        </div>
    );
}
