import React, { useState } from 'react';
import { PageContainer } from '../../Pagecontainer';
export function StudyGuide() {
    const [studyGuideData, setStudyGuideData] = useState({
        keyConcepts: ["Concept 1: Explanation", "Concept 2: Explanation"],
        definitions: ["Term 1: Definition", "Term 2: Definition"],
    });

    const handleSave = () => {
        localStorage.setItem('studyGuide', JSON.stringify(studyGuideData));
        alert("Study guide saved to local storage!");
    };
    return (
      <PageContainer>
           <div className="fixed inset-0 bg-gradient-to-br from-pink-300 via-pink-200 to-pink-400 flex items-center justify-center p-6">
            <div className="max-w-lg w-full bg-white rounded-lg shadow-lg p-8 text-center space-y-6">
                <h2 className="text-3xl font-semibold text-gray-800 mb-4">Your Study Guide</h2>

                <div className="space-y-6 text-left">
                    <div className="bg-pink-100 p-4 rounded-lg shadow-sm">
                        <h3 className="text-xl font-semibold text-pink-700 mb-2">Key Concepts</h3>
                        <ul className="text-gray-700 list-disc ml-5">
                            {studyGuideData.keyConcepts.map((concept, index) => (
                                <li key={index}>{concept}</li>
                            ))}
                        </ul>
                    </div>

                    <div className="bg-pink-100 p-4 rounded-lg shadow-sm">
                        <h3 className="text-xl font-semibold text-pink-700 mb-2">Definitions</h3>
                        <ul className="text-gray-700 list-disc ml-5">
                            {studyGuideData.definitions.map((definition, index) => (
                                <li key={index}>{definition}</li>
                            ))}
                        </ul>
                    </div>
                </div>

                <div className="flex justify-end space-x-4 mt-6">
                    <button
                        onClick={handleSave}
                        className="bg-pink-600 hover:bg-pink-700 text-white font-semibold py-2 px-6 rounded-lg transition duration-200 ease-in-out transform hover:scale-105"
                    >
                        Save
                    </button>
                    <button className="bg-gray-300 hover:bg-gray-400 text-gray-700 font-semibold py-2 px-6 rounded-lg">
                        Edit
                    </button>
                </div>
            </div>
        </div>
      </PageContainer>
  );
}
