import React, { useState }  from 'react';
import { PageContainer } from '../../Pagecontainer';
import { Link } from 'react-router-dom';
export function Summarize() {
    const [detailLevel, setDetailLevel] = useState(3);
    const [options, setOptions] = useState({
        keyPoints: false,
        definitions: false,
        examples: false,
    });
    const handleOptionChange = (e) => {
        const { id, checked } = e.target;
        setOptions((prevOptions) => ({ ...prevOptions, [id]: checked }));
    };
    return (
        <PageContainer>
            <h2 className="text-2xl font-semibold mb-4">Summarization Options</h2>
            <div className="space-y-4">
                <label className="block">
                    <span className="text-gray-700">Detail Level</span>
                    <input type="range" min="1" max="5" className="w-full mt-2" />
                </label>
                <div className="flex items-center space-x-2">
                    <input type="checkbox" id="keyPoints" />
                    <label htmlFor="keyPoints" className="text-gray-700">Key Points</label>
                </div>
                <div className="flex items-center space-x-2">
                    <input type="checkbox" id="definitions" />
                    <label htmlFor="definitions" className="text-gray-700">Definitions</label>
                </div>
                <div className="flex items-center space-x-2">
                    <input type="checkbox" id="examples" />
                    <label htmlFor="examples" className="text-gray-700">Examples</label>
                </div>
                <Link to="/studyguide">
                    <button className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 mt-4">
                        Generate Study Guide
                    </button>
                </Link>
            </div>
        </PageContainer>
    );
}
