import React from 'react';
import { PageContainer } from '../../Pagecontainer';

export function StudyGuide() {
    const [studyGuideData, setStudyGuideData] = useState("Sample study guide content...");

    // Save study guide to local storage
    const handleSave = () => {
        localStorage.setItem('studyGuide', studyGuideData);
        alert("Study guide saved to local storage!");
    };

    return (
        <div className="min-h-screen bg-gray-100 flex flex-col items-center p-4">
            <h2 className="text-2xl font-semibold mb-4">Generated Study Guide</h2>
            <div className="bg-white p-4 rounded shadow-md mb-4">
                <p className="text-gray-700">{studyGuideData}</p>
            </div>
            <div className="flex justify-end space-x-2 mt-4">
                <button 
                    onClick={handleSave}
                    className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
                >
                    Save
                </button>
                <button className="bg-gray-300 text-gray-700 px-4 py-2 rounded hover:bg-gray-400">
                    Edit
                </button>
            </div>
        </div>
    );
}
